import React , {useState} from 'react';
import './InputFiled.css';

interface ContainerProps {
    name: string;
    value:number;
  }


  const InputFiled: React.FC<ContainerProps> = (name , value) => {

    const [valueOut,setValue] = useState(value);

    const onChangeHanderl = () => {
      setValue(valueOut);
  }

    return (
      <div className="container">
        <strong>{name}</strong>
        <input type="number" value={valueOut} onChange={onChangeHanderl}></input>
      </div>
    );
  };

  export default InputFiled;
